package com.runbean.springboot01.controller;import com.runbean.springboot01.entity.User;import com.runbean.springboot01.service.XssService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import java.util.HashMap;import java.util.Map;@RestControllerpublic class controller {    @Autowired    private XssService xssService;    @GetMapping("/hello")    @ResponseBody    public String hello(String parameter){        System.out.println("进入 /hello");        System.out.println(parameter);        return parameter+"==<a href=\"http://www.baidu.com\">baidu</a>";    }    @PostMapping("/entity")    @ResponseBody    public User entity(@RequestBody User user){        System.out.println("进入 controller ......");        System.out.println(user);        return user;    }    @GetMapping("/xss")    @ResponseBody    public Object xss(){        Map<String,String> map = new HashMap<>();        map.put("msg",xssService.xss());        return map;    }}